import React from "react";
import axios from "axios";

function PostGet() {
    const [Id, setId] = React.useState(0);
    const [FirstName, setFirstName] = React.useState("");
    const [item, setItem] = React.useState();

    const onChangeId = (event: any) => {
        setId(event.target.value);
    };
    const onChangeFirstName = (event: any) => {
        setFirstName(event.target.value);
    };

    const onAddPerson = () => {
        //axios.post('https://localhost:44392/api/Person/person', {FirstName: FirstName});

        // axios({
        //     method: 'post',
        //     url: 'https://localhost:44392/api/Person/person',
        //     data: {FirstName},
        //     headers: {
        //         'accept': '*/*',
        //         'Content-Type': 'application/json',
        //     }
        // });
        fetch('/myserver.endpoint', {
            method: 'POST',
            body: JSON.stringify({
                FirstName: FirstName
            }),
            headers: {
                'Content-type': 'application/json; charset=UTF-8',
            },
        })
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                // Handle data
            })
            .catch((err) => {
                console.log(err.message);
            });
    }

    // React.useEffect(() => {
    //     axios.get('/api/Person/person').then((res) => {
    //         setItem(res.data);
    //     })
    // }, []);

    return(
        <div>
            <div>
                <input
                    type="text"
                    value={FirstName}
                    onChange={onChangeFirstName}
                    placeholder="FirstName"
                />
                <input
                    type="number"
                    value={Id}
                    onChange={onChangeId}
                    placeholder="Id"
                />
                <button onClick={onAddPerson} >Отправить</button>
                <button>Получить</button>
            </div>
            <span>{FirstName}   {Id}</span>
        </div>
    );
}

export default PostGet;
