import React from "react";
import axios from "axios";

function PostGet() {
    const [Id, setId] = React.useState(0);
    const [FirstName, setFirstName] = React.useState("");
    const [item, setItem] = React.useState({});

    const onChangeId = (event: any) => {
        setId(event.target.value);
    };
    const onChangeFirstName = (event: any) => {
        setFirstName(event.target.value);
    };

    const onAddPerson = () => {
        axios.post('https://localhost:44392/api/Person/person', FirstName);
    }
    const onGetPersons = () => {
        axios.get()
    }

     const onGetPerson = () => {
         fetch('https://localhost:44392/api/Person/ppp', {mode: 'no-cors'})
             .then((res) => res.json())
             .then((data) => {
                 console.log(data);
                 setItem(data);
             })
             .catch((err) => {
                 console.log(err.message);
             });
         console.log(item);
    }

    return(
        <div>
            <div>
                <input
                    type="text"
                    value={FirstName}
                    onChange={onChangeFirstName}
                    placeholder="FirstName"
                />
                <input
                    type="number"
                    value={Id}
                    onChange={onChangeId}
                    placeholder="Id"
                />
                <button onClick={onAddPerson} >Отправить</button>
                <button onClick={onGetPerson} >Получить</button>
            </div>
            <span>{FirstName}   {Id}</span>
            <p></p>
        </div>
    );
}

export default PostGet;
